mapper.select:
select
  code_id, record_date, recharge_amount, recharge_times, fee
from zf_code_record


        and code_id = #{codeId}


        and record_date = #{recordDate}


        and recharge_amount = #{rechargeAmount}


        and recharge_times = #{rechargeTimes}


        and fee = #{fee}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zf_code_record


        and code_id = #{codeId}


        and record_date = #{recordDate}


        and recharge_amount = #{rechargeAmount}


        and recharge_times = #{rechargeTimes}


        and fee = #{fee}
