mapper.select:
select
  merchant_id, balance, status, merchant_code, remark
from zf_merchant


        and merchant_id = #{merchantId}


        and balance = #{balance}


        and status = #{status}


        and merchant_code = #{merchantCode}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from zf_merchant


        and merchant_id = #{merchantId}


        and balance = #{balance}


        and status = #{status}


        and merchant_code = #{merchantCode}


        and remark = #{remark}

mapper.update:
update zf_merchant

        balance = balance + #{balance}

where merchant_id = #{merchantId}
