mapper.select:
select merchant_order_no, pay_amount, paid_amount, order_no, notify_url, code_id, create_time, update_time, merchant_id, channel_id, agent_id, order_status, notify_status, merchant_fee,pay_type
from zf_recharge
where order_no = #{orderNo}

select
  merchant_order_no, pay_amount, paid_amount, order_no, notify_url, code_id, create_time, update_time, merchant_id, channel_id, agent_id, order_status, notify_status, merchant_fee
from zf_recharge
where merchant_order_no = #{merchant_order_no}
and merchant_id = #{merchant_Id}

select
  merchant_order_no, pay_amount, paid_amount, order_no, notify_url, code_id, create_time, update_time, merchant_id, channel_id, agent_id, order_status, notify_status, merchant_fee
from zf_recharge
where pay_amount = #{amount}
and code_id = #{codeId}
and order_status = 1
and create_time  >=  DATE_SUB(#{transTime}, INTERVAL 10 MINUTE);

select
  merchant_order_no, pay_amount, paid_amount, order_no, notify_url, code_id, create_time, update_time, merchant_id, channel_id, agent_id, order_status, notify_status, merchant_fee
from zf_recharge

         order_status = #{orderStatus}

limit #{limit}, #{offset}

select count(1)
from zf_recharge


        and merchant_order_no = #{merchantOrderNo}


        and pay_amount = #{payAmount}


        and paid_amount = #{paidAmount}


        and order_no = #{orderNo}


        and notify_url = #{notifyUrl}


        and code_id = #{codeId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and merchant_id = #{merchantId}


        and channel_id = #{channelId}


        and agent_id = #{agentId}


        and order_status = #{orderStatus}


        and notify_status = #{notifyStatus}


        and merchant_fee = #{merchantFee}

mapper.update:
update zf_recharge
notify_status = 1, update_time = SYSDATE()
where merchant_order_no = #{merchantOrderNo}

update zf_recharge
notify_status = 2, update_time = SYSDATE()
where merchant_order_no = #{merchantOrderNo}
